	 @RequestMapping(
			 value="/blobRefSearch",method=RequestMethod.POST
	    ) 
	 @ResponseBody
	 public Map<String, Object> blobRefSearch(@RequestBody  RefViewModelVO refViewModelVO) {
		    Map<String, Object> results = new HashMap<String, Object>();
		    Map<String, String> params = new HashMap<String, String>();
		    
		    int pageNum = refViewModelVO.getRefClientPageInfo().getCurrPageIndex();

	        List<PrintVo> proList = new ArrayList<>();
	        String boArray = "";
	        Integer rows = null;
	        try {
	        	
	        	String tenantid = InvocationInfoProxy.getTenantid();
	        	params.put("tenantId", refViewModelVO.getTenantId()==null?tenantid:refViewModelVO.getTenantId());
	    		params.put("print_token", print_token);
	    		params.put("key", refViewModelVO.getContent());
	    		params.put("startIndex",String.valueOf(pageNum));
	    		params.put("count",String.valueOf(PAGESIZE));
	    		params.put("url", "/rest/bomanage/getbosbykey");
	    		String url = cloud_printUrl + "/print/doget";

		    	try {									
						boArray = HttpContextUtil.getInstance().doGetForStringWithContext(url, params);
					
					if (StringUtils.isNotEmpty(boArray)) {	
							
						JSONObject fromObject = JSONObject.fromObject(boArray);	
						if (fromObject.containsKey("recordCount")) {
							rows = Integer.parseInt(fromObject.getString("recordCount"));
						}
						if ( fromObject.containsKey("dataResult")) {
					
							JSONArray jsonArray2 = fromObject.getJSONArray("dataResult");
							for (Object object : jsonArray2) {
								PrintVo pv = new PrintVo();
								JSONObject fromObject1 = JSONObject.fromObject(object.toString());
								Object object2 = fromObject1.get("datamap");
								JSONObject fromObject2 = JSONObject.fromObject(object2.toString());
								String app_id = fromObject2.get("app_id").toString();
								String pk_bo = fromObject2.get("pk_bo").toString();
								String bo_code = fromObject2.get("bo_code").toString();
								String bo_name = fromObject2.get("bo_name").toString();
								String modifiedtiem = fromObject2.get("modifiedtiem").toString();
								String createtime = fromObject2.get("createtime").toString();
								pv.setApp_id(app_id);
								pv.setBo_code(bo_code);
								pv.setBo_name(bo_name);
								pv.setCreatetime(createtime);
								pv.setCreatetime(createtime);
								pv.setModifiedtiem(modifiedtiem);
								pv.setPk_bo(pk_bo);
								proList.add(pv);							
							 }	
						}
					 }	
		    	} catch (Exception e) {
		    			
		    			logger.error(e.getMessage(),e);
			}
				if (CollectionUtils.isNotEmpty(proList)) {
					 List<Map<String, String>> list = buildRtnValsOfRefTree(proList,refViewModelVO);
					
					 RefClientPageInfo refClientPageInfo = refViewModelVO.getRefClientPageInfo();
					 int pageSum=(rows-1)/PAGESIZE+1;
					 refClientPageInfo.setPageCount(pageSum);
//					 refClientPageInfo.setPageCount(10);
			         refClientPageInfo.setPageSize(50);
			         refViewModelVO.setRefClientPageInfo(refClientPageInfo);
				
			         results.put("dataList", list);
			         results.put("refViewModel", refViewModelVO);
				}
				
			} catch (Exception u) {
				logger.error(u.getMessage(),u);
			}
			
	        return results;		 
	 }

