	    private <T extends Serializable> List<T> filterData(List<T> datas, String resourceType, String matchKey, String clientParam) throws NoSuchFieldException,IllegalAccessException {
	        if(datas==null||datas.size()<1){
	            return new ArrayList<T>();
	        }
	        //如果不适用数据权限，则直接返回datas
	        boolean isUserDataPower=isUserDataPower(clientParam);
	        if(!isUserDataPower){
	            return datas;
	        }
	        //过滤数据权限
	        List<T> returnData=new ArrayList<T>();
	        List<DataPermission> dataPermissions = null;
	        try {
	            dataPermissions = AuthRbacClient.getInstance().queryDataPerms(InvocationInfoProxy.getTenantid(),InvocationInfoProxy.getSysid(),InvocationInfoProxy.getUserid(),resourceType);
	        }catch (com.yonyou.uap.ieop.security.exception.BusinessException e ){
	        	 logger.error(e.getMessage(),e);
	        }
	        Set<String> set=new HashSet<String>();
	        for(DataPermission dataPermission:dataPermissions){
	            set.add(dataPermission.getResourceId());
	        }
	        T t1=datas.get(0);

	        if(t1 instanceof Map){
	            for(T data:datas){
	                Map map= (Map) data;
	                if(set.contains(map.get(matchKey))){
	                    returnData.add(data);
	                }
	            }
	        }else{
	            Field field =t1.getClass().getDeclaredField(matchKey);
	            field.setAccessible(true);
	            for(T data:datas){
	                String metchKeyValue= (String) field.get(data);
	                if(set.contains(metchKeyValue)){
	                    returnData.add(data);
	                }
	            }
	        }
	        return returnData;
	    }
	  //TODO 这个方法的意思
	    private boolean isUserDataPower(String clientParam) {
	        // 管理员，默认全部权限
	        if (CommonUtil.isAdmin()) {
	            return false;
	        }
	        boolean isUserDataPower = false;
	        if (clientParam != null && clientParam.length() > 0) {
	            JSONObject json = JSONObject.fromObject(clientParam);
	            if (json.containsKey("isUseDataPower")) {
	                isUserDataPower = json.getBoolean("isUseDataPower");
	            }
	        }
	        return isUserDataPower;
	    }
